    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_recycler_view);
        RecyclerView recyclerView = findViewById(R.id.recyclerViewParent);

        foodsMenu();

        adapter = new recylerViewAdapter(this,foods);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        adapter.setClickListener(this);

    }

private void foodsMenu(){
        String[] foodsNameRv = getResources().getStringArray(R.array.foodNameRv);
        String[] foodsDescriptionRv = getResources().getStringArray(R.array.foodDescriptionRv);
         for(int i = 0; i < foodsNameRv.length;i++){
             foods.add(new Item(foodsNameRv[i],foodsDescriptionRv[i],foodImageURL[i]));
         }

}


    @Override
    public void onItemClick(View view, int position) {
        Toast.makeText(this, "You clicked " + adapter.getItem(position).getName() + " on row number " + position, Toast.LENGTH_SHORT).show();    }
